import React, { useState, useRef, useEffect } from 'react';
import { 
  Bell, 
  MessageCircle, 
  Send, 
  MapPin,
  AlertCircle, 
  CheckCircle 
} from 'lucide-react';

const RightSidebar = () => {
  const [activeTab, setActiveTab] = useState('map');
  const [notifications, setNotifications] = useState([
    {
      id: 1,
      type: 'alert',
      icon: AlertCircle,
      iconColor: 'text-yellow-500',
      message: 'Suspicious activity detected in Sector 3',
      timestamp: '2m ago',
      location: {
        lat: 40.7128,
        lng: -74.0060,
        name: 'Downtown Area'
      }
    },
    {
      id: 2,
      type: 'success',
      icon: CheckCircle,
      iconColor: 'text-green-500',
      message: 'Security checkpoint cleared',
      timestamp: '10m ago',
      location: {
        lat: 40.7150,
        lng: -74.0080,
        name: 'North Entrance'
      }
    }
  ]);

  const renderMap = () => {
    // In a real implementation, this would use a mapping library like Leaflet or Google Maps
    return (
      <div className="p-4 h-full">
        <h2 className="text-lg font-semibold mb-4">Event Locations</h2>
        <div className="bg-gray-100 h-64 rounded flex items-center justify-center">
          <MapPin className="text-blue-500" size={48} />
          <p className="ml-2">Geospatial Map Placeholder</p>
        </div>
        <div className="mt-4">
          <h3 className="font-semibold mb-2">Active Locations</h3>
          {notifications.map(notification => (
            <div 
              key={notification.id} 
              className="flex items-center bg-white p-3 rounded-lg mb-2 shadow-sm"
            >
              <MapPin className="mr-3 text-blue-500" size={20} />
              <div>
                <p className="text-sm font-medium">{notification.location.name}</p>
                <p className="text-xs text-gray-500">
                  {notification.message}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="w-96 bg-white border-l h-screen flex flex-col">
      {/* Tabs */}
      <div className="flex border-b">
        <button 
          onClick={() => setActiveTab('map')}
          className={`flex-1 p-4 flex items-center justify-center ${
            activeTab === 'map' ? 'bg-gray-100' : ''
          }`}
        >
          <MapPin className="mr-2" size={20} />
          Geospatial Map
        </button>
        <button 
          onClick={() => setActiveTab('notifications')}
          className={`flex-1 p-4 flex items-center justify-center ${
            activeTab === 'notifications' ? 'bg-gray-100' : ''
          }`}
        >
          <Bell className="mr-2" size={20} />
          Notifications
        </button>
      </div>

      {/* Content */}
      {activeTab === 'map' && renderMap()}

      {activeTab === 'notifications' && (
        <div className="p-4 overflow-y-auto">
          <h2 className="text-lg font-semibold mb-4">Notifications</h2>
          {notifications.map(notification => (
            <div 
              key={notification.id} 
              className="flex items-center bg-gray-50 p-3 rounded-lg mb-2"
            >
              <notification.icon 
                className={`mr-3 ${notification.iconColor}`} 
                size={24} 
              />
              <div className="flex-grow">
                <p className="text-sm">{notification.message}</p>
                <p className="text-xs text-gray-500">
                  {notification.timestamp} | {notification.location.name}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default RightSidebar;
