import React, { useState, useRef, useEffect } from 'react';
import { 
  Bell, 
  MapPin, 
  AlertTriangle, 
  Eye, 
  Send 
} from 'lucide-react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const RightSidebar = ({ notifications = [], selectedFrames = [] }) => {
  const [activeTab, setActiveTab] = useState('map');
  const [chatInput, setChatInput] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const chatEndRef = useRef(null);

  const handleChatSubmit = (e) => {
    e.preventDefault();
    if (!chatInput.trim()) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      text: chatInput
    };
    setChatMessages(prev => [...prev, userMessage]);

    // Simulate AI-assisted response for surveillance context
    const aiResponse = {
      id: Date.now() + 1,
      type: 'ai',
      text: processAIResponse(chatInput)
    };
    setChatMessages(prev => [...prev, aiResponse]);

    setChatInput('');
  };

  const processAIResponse = (input) => {
    // Context-aware AI response simulation
    if (input.toLowerCase().includes('suspicious')) {
      return 'Analyzing recent event logs for suspicious activities...';
    }
    return 'Understood. Searching surveillance records.';
  };

  return (
    <div className="w-96 bg-white border-l h-screen flex flex-col">
      {/* Tabs */}
      <div className="flex border-b">
        <button 
          onClick={() => setActiveTab('map')}
          className={`flex-1 p-4 flex items-center justify-center ${
            activeTab === 'map' ? 'bg-gray-100' : ''
          }`}
        >
          <MapPin className="mr-2" size={20} />
          Location
        </button>
        <button 
          onClick={() => setActiveTab('events')}
          className={`flex-1 p-4 flex items-center justify-center ${
            activeTab === 'events' ? 'bg-gray-100' : ''
          }`}
        >
          <AlertTriangle className="mr-2" size={20} />
          Event Log
        </button>
        <button 
          onClick={() => setActiveTab('chat')}
          className={`flex-1 p-4 flex items-center justify-center ${
            activeTab === 'chat' ? 'bg-gray-100' : ''
          }`}
        >
          <Send className="mr-2" size={20} />
          Chat
        </button>
      </div>

      {/* Content */}
      {activeTab === 'map' && (
        <div className="h-full w-full p-2">
          <MapContainer 
            center={[37.7749, -122.4194]} 
            zoom={13} 
            className="h-96 w-full"
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {notifications.map((event, index) => (
              <Marker 
                key={index} 
                position={[event.coordinates.lat, event.coordinates.lng]}
              >
                <Popup>
                  <div>
                    <strong>{event.type}</strong>
                    <p>Confidence: {(event.confidence * 100).toFixed(2)}%</p>
                    <p>Timestamp: {event.timestamp}</p>
                    <p>Location: {event.location}</p>
                  </div>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </div>
      )}

      {activeTab === 'events' && (
        <div className="p-4 overflow-y-auto">
          <h2 className="text-lg font-semibold mb-4">Detected Events</h2>
          {notifications.map((event, index) => (
            <div 
              key={index} 
              className="flex items-center bg-gray-50 p-3 rounded-lg mb-2"
            >
              <AlertTriangle 
                className={`mr-3 ${
                  event.confidence > 0.8 
                    ? 'text-red-500' 
                    : 'text-yellow-500'
                }`} 
                size={24} 
              />
              <div className="flex-grow">
                <p className="text-sm font-medium">{event.type}</p>
                <p className="text-xs text-gray-500">
                  {event.location} • {event.timestamp}
                </p>
                <div className="text-xs mt-1">
                  Confidence: {(event.confidence * 100).toFixed(2)}%
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {activeTab === 'chat' && (
        <div className="flex flex-col h-full">
          {/* Chat Messages */}
          <div className="flex-grow p-4 overflow-y-auto space-y-3">
            {chatMessages.map(message => (
              <div 
                key={message.id}
                className={`flex ${
                  message.type === 'user' ? 'justify-end' : 'justify-start'
                }`}
              >
                <div 
                  className={`p-3 rounded-lg max-w-[70%] ${
                    message.type === 'user' 
                      ? 'bg-blue-500 text-white' 
                      : 'bg-gray-200 text-black'
                  }`}
                >
                  {message.text}
                </div>
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>

          {/* Chat Input */}
          <form 
            onSubmit={handleChatSubmit}
            className="p-4 border-t flex items-center"
          >
            <input 
              type="text"
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="Ask about events or surveillance..."
              className="flex-grow mr-2 p-2 border rounded"
            />
            <button 
              type="submit" 
              className="bg-blue-500 text-white p-2 rounded"
            >
              <Send size={20} />
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default RightSidebar;
