import React, { useState, useEffect } from 'react';

const VideoGrid = ({ videos = [] }) => {
  const [processedVideos, setProcessedVideos] = useState([]);

  useEffect(() => {
    // Process videos to extract duration and generate thumbnails
    const processVideos = async () => {
      const processedVideoPromises = videos.map(video => {
        return new Promise((resolve) => {
          // If thumbnail already exists (from URL.createObjectURL), use it
          if (video.thumbnail && video.thumbnail.startsWith('blob:')) {
            resolve(video);
            return;
          }

          // Create a video element to get duration and thumbnail
          const videoElement = document.createElement('video');
          videoElement.preload = 'metadata';
          videoElement.src = URL.createObjectURL(video.file);

          videoElement.onloadedmetadata = () => {
            // Create canvas to generate thumbnail
            const canvas = document.createElement('canvas');
            canvas.width = videoElement.videoWidth;
            canvas.height = videoElement.videoHeight;

            // Seek to middle of the video to capture a representative frame
            videoElement.currentTime = videoElement.duration / 2;

            videoElement.onseeked = () => {
              // Draw video frame on canvas
              const context = canvas.getContext('2d');
              context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);

              // Convert canvas to thumbnail
              const thumbnailDataUrl = canvas.toDataURL('image/jpeg');

              // Convert seconds to MM:SS format
              const minutes = Math.floor(videoElement.duration / 60);
              const seconds = Math.floor(videoElement.duration % 60);

              resolve({
                ...video,
                duration: `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`,
                thumbnail: thumbnailDataUrl
              });

              // Revoke object URLs to free memory
              URL.revokeObjectURL(videoElement.src);
            };
          };
        });
      });

      // Wait for all videos to be processed
      const processedVideoResults = await Promise.all(processedVideoPromises);
      setProcessedVideos(processedVideoResults);
    };

    // Only process if there are new videos
    if (videos.length > 0) {
      processVideos();
    }
  }, [videos]);

  // Combine processed videos with sample videos
  const allVideos = [
    ...processedVideos,
    { 
      title: "Usain Bolt Olympic Record", 
      duration: "00:13:20",
      thumbnail: "https://via.placeholder.com/300x200"
    },
    { 
      title: "Tennis Grand Slam Highlights", 
      duration: "00:01:14",
      thumbnail: "https://via.placeholder.com/300x200"
    },
    { 
      title: "Soccer World Cup Moment", 
      duration: "00:09:09",
      thumbnail: "https://via.placeholder.com/300x200"
    }
  ];

  return (
    <div>
      <div className="flex justify-between items-center mb-4">
        <div className="text-gray-600">
          Videos â€¢ {allVideos.length} videos
        </div>
        <div className="text-gray-600">
          Index created on Jan 13, 2025
        </div>
      </div>

      <div className="grid grid-cols-3 gap-4">
        {allVideos.map((video, index) => (
          <div key={index} className="border rounded overflow-hidden">
            <img 
              src={video.thumbnail} 
              alt={video.title} 
              className="w-full h-48 object-cover" 
            />
            <div className="p-3">
              <div className="font-semibold text-sm truncate">
                {video.title || video.file?.name || `Video ${index + 1}`}
              </div>
              <div className="text-xs text-gray-500">
                {video.duration || 'Unknown duration'}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default VideoGrid;
